import React from 'react';
import {getAllGraphPosts} from "../utils/graph";
import {BlogPostPreview} from "./BlogPostPreview";
import styled from 'styled-components';



const BlogWrapper = styled.div`
    display: grid;
    grid-auto-rows: 400px;
    grid-template-columns: repeat(3,30%);
    grid-gap: 40px 40px;
    width: 100vw;
    padding: 40px;
`

export class Blog extends React.Component {


    constructor(props) {
        super(props);


        this.state = {
            blogPostsIds: [],
            siteId: "a402fab0-dfc9-11e9-815b-41f22eba7175",
            activeId: false
        }

        this.fetchBlogPost = this.fetchBlogPost.bind(this);
        this.updateActiveId = this.updateActiveId.bind(this);
    }

    getBlogPostsIds() {

        getAllGraphPosts().then( res => {
            console.log(res)
            this.setState({blogPostsIds:res.data.blogPosts})
        })

    }
